import React, { useEffect, useState } from 'react';

function App(){

  const [movies, setMovies] = useState([]);

  useEffect(() => {
    fetch('/movies')
    .then((response) => {
      if (!response.ok) {
        throw new Error('HTTP error!');
      }
      return response.json();
    })
    .then((data) => {
      setMovies(data);
      console.log(data);
    })
    .catch((error) => {
      console.error('Error fetching the data:', error);
    })
  }, [])

}

export default App
